import React, { Component } from "react";
import studentDataService from "../services/student.service";
//import examDataService from "../services/exam.service";
import { Link } from "react-router-dom";
import Select from 'react-select';
import "./addExam.css";

const checkboxes = [
  'Math', 'Kiswahili', 'English', 'Physics'
];
export default class addExam extends Component {
    state = {
      checkedItems: new Map(),
    }
   handleChange=(e)=> {
    const item = e.target.name;
    const isChecked = e.target.checked;
    this.setState(prevState => ({ checkedItems: prevState.checkedItems.set(item, isChecked) }));
    console.log(item);
    }

  render() {   
    console.log(this.state.checkedItems) 
    return (
      <>
        {
          checkboxes.map(item => (
            <div className="row">
                <div className="col-md-2">
                    <input type="checkbox"
                    checked={this.state.checkedItems.get(item)} 
                    onChange={this.handleChange} 
                    name={item}/>
                    <label key={item}>
                    {item}
                    </label>
                </div>
                <div className="col-md-2">
                    <input
                        type="text"
                        className="form-control"
                        id="paper1"
                        required
                        //value={this.state.reportTime}
                        //onChange={this.onChangeReportTime}
                        name="paper1"
                        placeholder="Paper 1 Full Score"
                    />
                </div>
                <div className="col-md-2">
                    <input
                        type="text"
                        className="form-control"
                        id="paper2"
                        required
                        //value={this.state.reportTime}
                        //onChange={this.onChangeReportTime}
                        name="paper2"
                        placeholder="Paper 2 Full Score"
                        marginBottom="10px"
                    />
                </div>
                <div className="col-md-2">
                    <input
                        type="text"
                        className="form-control"
                        id="paper3"
                        required
                        //value={this.state.reportTime}
                        //onChange={this.onChangeReportTime}
                        name="paper3"
                        placeholder="Paper 3 Full Score"
                        marginBottom="10px"
                    />
                </div>
            </div>
          ))
        }
      </>
    );
  }
}
