import React, { Component } from "react";
import reportDataService from "../services/report.service";

export default class addReport extends Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeComments = this.onChangeComments.bind(this);
    this.onChangeTeacher = this.onChangeTeacher.bind(this);
    this.saveReport = this.saveReport.bind(this);
    this.newReport = this.newReport.bind(this);

    this.state = {
      id: null,
      title: "",
      form: "", 
      graduationYear: "",
      graduated: false,
      submitted: false
    };
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value
    });
  }

  onChangeComments(e) {
    this.setState({
      form: e.target.value
    });
  }

  onChangeTeacher(e) {
    this.setState({
      graduationYear: e.target.value
    });
  }

  saveReport() {
    var data = {
      title: this.state.title,
      form: this.state.form,
      graduationYear: this.state.graduationYear
    };

    reportDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          title: response.data.title,
          form: response.data.form,
          graduationYear: response.data.graduationYear,
          graduated: response.data.graduated,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newReport() {
    this.setState({
      id: null,
      title: "",
      form: "",
      graduationYear: "",
      graduated: false,

      submitted: false
    });
  }

  render() {
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newReport}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                className="form-control"
                id="title"
                required
                value={this.state.title}
                onChange={this.onChangeTitle}
                name="title"
              />
            </div>

            <div className="form-group">
              <label htmlFor="form">Comments</label>
              <input
                type="text"
                className="form-control"
                id="form"
                required
                value={this.state.form}
                onChange={this.onChangeComments}
                name="form"
              />
            </div>

            <div className="form-group">
              <label htmlFor="graduationYear">Teacher</label>
              <input
                type="text"
                className="form-control"
                id="graduationYear"
                required
                value={this.state.graduationYear}
                onChange={this.onChangeTeacher}
                name="graduationYear"
              />
            </div>

            <button onClick={this.saveReport} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
