import React, { Component } from "react";
import studentDataService from "../services/student.service";

export default class student extends Component {
  constructor(props) {
    super(props);
    this.onChangename = this.onChangename.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.getstudent = this.getstudent.bind(this);
    this.updatePublished = this.updatePublished.bind(this);
    this.updatestudent = this.updatestudent.bind(this);
    this.deletestudent = this.deletestudent.bind(this);

    this.state = {
      currentstudent: {
        id: null,
        name: "",
        description: "",
        published: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getstudent(this.props.match.params.id);
  }

  onChangename(e) {
    const name = e.target.value;

    this.setState(function(prevState) {
      return {
        currentstudent: {
          ...prevState.currentstudent,
          name: name
        }
      };
    });
  }

  onChangeDescription(e) {
    const description = e.target.value;

    this.setState(prevState => ({
      currentstudent: {
        ...prevState.currentstudent,
        description: description
      }
    }));
  }

  getstudent(id) {
    studentDataService.get(id)
      .then(response => {
        this.setState({
          currentstudent: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updatePublished(status) {
    var data = {
      id: this.state.currentstudent.id,
      name: this.state.currentstudent.name,
      description: this.state.currentstudent.description,
      published: status
    };

    studentDataService.update(this.state.currentstudent.id, data)
      .then(response => {
        this.setState(prevState => ({
          currentstudent: {
            ...prevState.currentstudent,
            published: status
          }
        }));
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updatestudent() {
    studentDataService.update(
      this.state.currentstudent.id,
      this.state.currentstudent
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The student info was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deletestudent() {    
    studentDataService.delete(this.state.currentstudent.id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/students')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentstudent } = this.state;

    return (
      <div>
        {currentstudent ? (
          <div className="edit-form">
            <h4>Student</h4>
            <form>
              <div className="form-group">
                <label htmlFor="name">Full Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  value={currentstudent.name}
                  onChange={this.onChangename}
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Form</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentstudent.description}
                  onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label>
                  <strong>Status:</strong>
                </label>
                {currentstudent.published ? "Graduated" : "In School"}
              </div>
            </form>

            {currentstudent.published ? (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updatePublished(false)}
              >
                In School
              </button>
            ) : (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updatePublished(true)}
              >
                Graduated
              </button>
            )}

            <button
              className="badge badge-danger mr-2"
              onClick={this.deletestudent}
            >
              Delete
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updatestudent}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click to select a student...</p>
          </div>
        )}
      </div>
    );
  }
}
